beta10 = np.linalg.solve(normal_coef10, normal_vect10)
MSE10 = (1 / 629) * np.linalg.norm(X10 @ beta10 - Y) ** 2


def f10(t):
    return np.dot(beta10, np.array(row_func(t, 1000)))


